import { useEffect, useState } from "react";
import { Chart } from "react-google-charts";

import _ from "lodash";
import axios from "axios";
import "./App.css";

export const data = [
  [
    "Element",
    "Density",
    { role: "style" },
    {
      sourceColumn: 0,
      role: "annotation",
      type: "string",
      calc: "stringify",
    },
  ],
  ["Copper", 8.94, "#b87333", null],
  ["Silver", 10.49, "silver", null],
  ["Gold", 19.3, "gold", null],
  ["Platinum", 21.45, "color: #e5e4e2", null],
];

export const options = {
  title: "Chamados em Aberto",
  width: 600,
  height: 400,
  bar: { groupWidth: "95%" },
  legend: { position: "none" },
};

const App = () => {
  const [dados, setDados] = useState([]);

  // data.data.registros.map((teste) => (
  //   console.log(teste)
  // ))

  useEffect(() => {
    const delayDebounceFn = setTimeout(() => {
      const fetchData = async () => {
        try {
          const data = await axios(
            "https://apichamados.globallive.com.br/chamados"
          );

          const values = _.groupBy(data.data.registros, (value) => {
            // console.log(value.data_abertura[12])
            return value.data_abertura.substring(0, 10);
          });

          const result = _.map(values, (value, key) => [
            key,
            _.sumBy(values[key], (v) => v.sales),
            "#b87333",
            null
          ]);

          console.log(result)
          setDados(values);
          console.log("values", values);
        } catch (error) {
          console.log(error);
        }
      };
      fetchData();
    }, 500);
  }, []);

  return (
    <div className="App">
      <h1>Gráficos em React</h1>
      <Chart
        chartType="BarChart"
        width="100%"
        height="100%"
        data={dados}
        options={options}
      />
    </div>
  );
};

export default App;
